import React, { useState, useEffect } from "react";
import { useAuth } from "../context/AuthContext";
import { userService, bookingService } from "../services/api";
import "../styles/UserProfile.css";

const UserProfile = () => {
    const { user, logout } = useAuth();
    const [profile, setProfile] = useState(null);
    const [bookings, setBookings] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState("");
    const [editMode, setEditMode] = useState(false);
    const [formData, setFormData] = useState({});

    useEffect(() => {
        fetchProfile();
        fetchBookings();
    }, []);

    const fetchProfile = async () => {
        try {
            const response = await userService.getUserById(user.id);
            setProfile(response.data.data);
            setFormData(response.data.data);
        } catch (err) {
            setError(err.response?.data?.message || "Failed to fetch profile");
        } finally {
            setLoading(false);
        }
    };

    const fetchBookings = async () => {
        try {
            const response = await bookingService.getBookings({ limit: 100 });
            setBookings(response.data.data);
        } catch (err) {
            console.error("Failed to fetch bookings:", err);
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleUpdate = async () => {
        try {
            await userService.updateUser(user.id, {
                name: formData.name,
                email: formData.email
            });
            setProfile(formData);
            setEditMode(false);
            alert("Profile updated successfully!");
        } catch (err) {
            setError(err.response?.data?.message || "Failed to update profile");
        }
    };

    if (loading) return <div className="loading">Loading profile...</div>;

    return (
        <div className="profile-container">
            <div className="profile-header">
                <h2>My Profile</h2>
            </div>

            {error && <div className="error-message">{error}</div>}

            <div className="profile-section">
                <h3>Personal Information</h3>
                {!editMode ? (
                    <div className="profile-info">
                        <p><strong>Name:</strong> {profile?.name}</p>
                        <p><strong>Email:</strong> {profile?.email}</p>
                        <p><strong>Role:</strong> <span className="badge">{profile?.role}</span></p>
                        <button onClick={() => setEditMode(true)} className="edit-btn">
                            Edit Profile
                        </button>
                    </div>
                ) : (
                    <div className="profile-form">
                        <div className="form-group">
                            <label>Name</label>
                            <input
                                type="text"
                                name="name"
                                value={formData.name || ""}
                                onChange={handleChange}
                            />
                        </div>
                        <div className="form-group">
                            <label>Email</label>
                            <input
                                type="email"
                                name="email"
                                value={formData.email || ""}
                                onChange={handleChange}
                            />
                        </div>
                        <button onClick={handleUpdate} className="save-btn">Save Changes</button>
                        <button onClick={() => setEditMode(false)} className="cancel-btn">Cancel</button>
                    </div>
                )}
            </div>

            <div className="bookings-section">
                <h3>My Bookings</h3>
                {bookings.length > 0 ? (
                    <div className="bookings-list">
                        {bookings.map(booking => (
                            <div key={booking._id} className="booking-card">
                                <h4>{booking.property.title}</h4>
                                <p><strong>Status:</strong> <span className={`status-${booking.status.toLowerCase()}`}>{booking.status}</span></p>
                                <p><strong>Visit Date:</strong> {new Date(booking.visitDate).toLocaleDateString()}</p>
                                <p><strong>Message:</strong> {booking.message}</p>
                            </div>
                        ))}
                    </div>
                ) : (
                    <p>No bookings yet</p>
                )}
            </div>

            <div className="profile-actions">
                <button onClick={logout} className="logout-btn">Logout</button>
            </div>
        </div>
    );
};

export default UserProfile;
