import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import { propertyService, bookingService, reviewService } from "../services/api";
import ReviewSection from "../components/ReviewSection";
import BookingForm from "../components/BookingForm";
import "../styles/PropertyDetails.css";

const PropertyDetails = () => {
    const { id } = useParams();
    const { isAuthenticated } = useAuth();
    const [property, setProperty] = useState(null);
    const [reviews, setReviews] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState("");
    const [showBookingForm, setShowBookingForm] = useState(false);

    useEffect(() => {
        fetchProperty();
        fetchReviews();
    }, [id]);

    const fetchProperty = async () => {
        try {
            const response = await propertyService.getPropertyById(id);
            setProperty(response.data.data);
        } catch (err) {
            setError(err.response?.data?.message || "Failed to fetch property");
        } finally {
            setLoading(false);
        }
    };

    const fetchReviews = async () => {
        try {
            const response = await reviewService.getReviewsByProperty(id, { limit: 10 });
            setReviews(response.data.data);
        } catch (err) {
            console.error("Failed to fetch reviews:", err);
        }
    };

    const handleBookingSuccess = () => {
        setShowBookingForm(false);
        alert("Booking request submitted successfully!");
    };

    const handleReviewAdded = () => {
        fetchReviews();
    };

    if (loading) return <div className="loading">Loading property details...</div>;
    if (error) return <div className="error-message">{error}</div>;
    if (!property) return <div className="error-message">Property not found</div>;

    return (
        <div className="property-details-container">
            <div className="property-header">
                <h1>{property.title}</h1>
                <p className="price">₹ {property.price.toLocaleString()}</p>
            </div>

            <div className="property-content">
                <div className="property-main">
                    <div className="property-image">
                        <div className="placeholder-image">Property Image</div>
                    </div>

                    <div className="property-info">
                        <h3>Property Details</h3>
                        <p><strong>Type:</strong> {property.propertyType}</p>
                        <p><strong>Status:</strong> <span className={`status-${property.status.toLowerCase()}`}>{property.status}</span></p>
                        <p><strong>Address:</strong> {property.location.address}</p>
                        <p><strong>City:</strong> {property.location.city}, {property.location.state}</p>
                        <p><strong>Pincode:</strong> {property.location.pincode}</p>
                        <p><strong>Owner:</strong> {property.owner.name}</p>
                        <p><strong>Contact:</strong> {property.owner.email}</p>

                        <div className="description">
                            <h4>Description</h4>
                            <p>{property.description}</p>
                        </div>

                        {isAuthenticated && property.status === "Available" && (
                            <button
                                className="booking-btn"
                                onClick={() => setShowBookingForm(!showBookingForm)}
                            >
                                {showBookingForm ? "Cancel" : "Book Now"}
                            </button>
                        )}
                    </div>
                </div>

                <div className="property-sidebar">
                    {showBookingForm && isAuthenticated && (
                        <BookingForm
                            propertyId={id}
                            onSuccess={handleBookingSuccess}
                        />
                    )}

                    {!isAuthenticated && (
                        <div className="login-prompt">
                            <p>Please login to book this property</p>
                            <a href="/login" className="btn">Login</a>
                        </div>
                    )}
                </div>
            </div>

            <div className="reviews-section">
                <h3>Reviews & Ratings</h3>
                {isAuthenticated && (
                    <ReviewSection
                        propertyId={id}
                        onReviewAdded={handleReviewAdded}
                    />
                )}
                <div className="reviews-list">
                    {reviews.length > 0 ? (
                        reviews.map(review => (
                            <div key={review._id} className="review-item">
                                <div className="review-header">
                                    <strong>{review.user.name}</strong>
                                    <span className="rating">⭐ {review.rating}/5</span>
                                </div>
                                <p>{review.comment}</p>
                            </div>
                        ))
                    ) : (
                        <p>No reviews yet</p>
                    )}
                </div>
            </div>
        </div>
    );
};

export default PropertyDetails;
