import React from "react";
import { Link } from "react-router-dom";
import "../styles/PropertyCard.css";

const PropertyCard = ({ property }) => {
    // Handle undefined or invalid property
    if (!property || !property._id) {
        return null;
    }

    const { location = {}, description = "", price = 0, status = "Available" } = property;
    const descriptionPreview = description.substring ? description.substring(0, 80) : "";

    return (
        <Link to={`/properties/${property._id}`} className="property-card-link">
            <div className="property-card">
                <div className="property-image">
                    <div className="placeholder-image">Property Image</div>
                    <span className="status-badge">{status}</span>
                </div>

                <div className="property-card-content">
                    <h3>{property.title || "Untitled Property"}</h3>
                    
                    <div className="property-type-location">
                        <span className="property-type">{property.propertyType || "N/A"}</span>
                        <span className="location">
                            {location.city || "City"}, {location.state || "State"}
                        </span>
                    </div>

                    <p className="description">{descriptionPreview}...</p>

                    <div className="property-footer">
                        <div className="price">â‚¹ {price.toLocaleString()}</div>
                        <button className="view-btn">View Details</button>
                    </div>
                </div>
            </div>
        </Link>
    );
};

export default PropertyCard;
