import React, { useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import { propertyService, reviewService } from "../services/api";
import ReviewSection from "../components/ReviewSection";
import BookingForm from "../components/BookingForm";
import { FaHome, FaMapMarkerAlt, FaUser, FaEnvelope, FaTag, FaCheckCircle, FaStar } from "react-icons/fa";
import "../styles/PropertyDetails.css";

const PropertyDetails = () => {
    const { id } = useParams();
    const { isAuthenticated, user: currentUser } = useAuth();
    const [property, setProperty] = useState(null);
    const [reviews, setReviews] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState("");
    const [showBookingForm, setShowBookingForm] = useState(false);

    useEffect(() => {
        fetchProperty();
        fetchReviews();
    }, [id]);

    const fetchProperty = async () => {
        try {
            const response = await propertyService.getPropertyById(id);
            setProperty(response.data.data);
        } catch (err) {
            setError(err.response?.data?.message || "Failed to fetch property");
        } finally {
            setLoading(false);
        }
    };

    const fetchReviews = async () => {
        try {
            const response = await reviewService.getReviewsByProperty(id, { limit: 10 });
            setReviews(response.data.data);
        } catch (err) {
            console.error("Failed to fetch reviews:", err);
        }
    };

    const handleBookingSuccess = () => {
        setShowBookingForm(false);
        alert("Booking request submitted successfully!");
    };

    const handleReviewAdded = () => {
        fetchReviews();
    };

    if (loading) return (
        <div className="loading-container">
            <div className="loading-spinner"></div>
            <p>Loading premium property details...</p>
        </div>
    );

    if (error) return (
        <div className="error-container">
            <div className="error-icon">⚠️</div>
            <p>{error}</p>
            <Link to="/properties" className="btn-details">Back to Listings</Link>
        </div>
    );

    if (!property) return (
        <div className="error-container">
            <p>Property not found or has been removed.</p>
            <Link to="/properties" className="btn-details">Back to Listings</Link>
        </div>
    );

    const imageUrl = property.images?.length > 0
        ? property.images[0]
        : "https://images.unsplash.com/photo-1570129477492-45c003edd2be?ixlib=rb-4.0.3&auto=format&fit=crop&w=1200&q=80";

    const price = property.price ? property.price.toLocaleString() : "Contact for Price";
    const city = property.location?.city || "Location Not Specified";
    const state = property.location?.state || "";
    const ownerName = property.owner?.name || "Verified Agent";
    const ownerEmail = property.owner?.email || "Contact via portal";

    return (
        <div className="property-details-container">
            <div className="property-header">
                <h1>{property.title}</h1>
                <div className="price">₹ {price}</div>
            </div>

            <div className="property-content">
                <div className="property-main">
                    <div className="property-image-gallery">
                        <img src={imageUrl} alt={property.title} />
                    </div>

                    <div className="property-info-card">
                        <h3>Property Overview</h3>
                        <div className="info-grid">
                            <div className="info-item">
                                <FaHome />
                                <div>
                                    <span className="label">Type</span>
                                    <span className="value">{property.propertyType}</span>
                                </div>
                            </div>
                            <div className="info-item">
                                <FaTag />
                                <div>
                                    <span className="label">Status</span>
                                    <span className="value">{property.status}</span>
                                </div>
                            </div>
                            <div className="info-item">
                                <FaMapMarkerAlt />
                                <div>
                                    <span className="label">Location</span>
                                    <span className="value">{city}{state ? `, ${state}` : ""}</span>
                                </div>
                            </div>
                            <div className="info-item">
                                <FaCheckCircle />
                                <div>
                                    <span className="label">Pincode</span>
                                    <span className="value">{property.location?.pincode || "N/A"}</span>
                                </div>
                            </div>
                        </div>

                        <div className="owner-info">
                            <div className="owner-avatar">
                                <FaUser />
                            </div>
                            <div className="owner-details">
                                <div className="info-item-simple">
                                    <span className="label">Listed By</span>
                                    <span className="value">{ownerName}</span>
                                </div>
                                <div className="info-item-simple">
                                    <span className="label">Contact</span>
                                    <span className="value">{ownerEmail}</span>
                                </div>
                            </div>
                        </div>

                        <div className="description-section">
                            <h4>Description</h4>
                            <p>{property.description}</p>
                        </div>
                    </div>
                </div>

                <div className="property-sidebar">
                    {isAuthenticated ? (
                        property.status === "Available" && property.owner?._id !== currentUser?.id && (
                            <div className="booking-card">
                                <h4>Schedule a Visit</h4>
                                <p>Interested in this property? Book a professional tour today.</p>
                                <button
                                    className="booking-btn"
                                    onClick={() => setShowBookingForm(!showBookingForm)}
                                >
                                    {showBookingForm ? "Cancel Request" : "Register Interest"}
                                </button>

                                {showBookingForm && (
                                    <div className="form-overlay-inline">
                                        <BookingForm
                                            propertyId={id}
                                            onSuccess={handleBookingSuccess}
                                        />
                                    </div>
                                )}
                            </div>
                        )
                    ) : (
                        <div className="login-prompt">
                            <h4>Ready for a visit?</h4>
                            <p>Please sign in to schedule a viewing or contact the agent directly.</p>
                            <Link to="/login" className="btn-details" style={{ display: 'block', textAlign: 'center', marginTop: '15px' }}>
                                Sign In to Continue
                            </Link>
                        </div>
                    )}

                    {currentUser?.id === property.owner?._id && (
                        <div className="owner-actions-card">
                            <h4>Your Listing</h4>
                            <p>You are the owner of this property. Manage your listing from the dashboard.</p>
                            <Link to="/manage-listings" className="btn-details" style={{ display: 'block', textAlign: 'center', marginTop: '15px', background: 'var(--accent-color)' }}>
                                Manage Property
                            </Link>
                        </div>
                    )}
                </div>
            </div>

            <div className="reviews-section">
                <div className="section-header">
                    <h3>Guest Reviews</h3>
                    <div className="rating-summary">
                        <FaStar color="var(--warning-color)" />
                        <span>{reviews.length > 0 ? (reviews.reduce((acc, r) => acc + r.rating, 0) / reviews.length).toFixed(1) : "N/A"}</span>
                        <small>({reviews.length} reviews)</small>
                    </div>
                </div>

                {isAuthenticated && property.owner?._id !== currentUser?.id && (
                    <div className="add-review-box">
                        <ReviewSection
                            propertyId={id}
                            onReviewAdded={handleReviewAdded}
                        />
                    </div>
                )}

                <div className="reviews-list">
                    {reviews.length > 0 ? (
                        reviews.map(review => (
                            <div key={review._id} className="review-item">
                                <div className="review-header">
                                    <div className="user-info">
                                        <div className="user-avatar-mini">{review.user?.name?.charAt(0) || "U"}</div>
                                        <span className="review-user">{review.user?.name || "Community Member"}</span>
                                    </div>
                                    <span className="review-rating">
                                        {[...Array(5)].map((_, i) => (
                                            <FaStar key={i} color={i < review.rating ? "var(--warning-color)" : "#e2e8f0"} />
                                        ))}
                                    </span>
                                </div>
                                <p className="review-comment">{review.comment}</p>
                            </div>
                        ))
                    ) : (
                        <div className="no-reviews">
                            <p>No reviews yet for this premium listing. Be the first to share your thoughts!</p>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default PropertyDetails;
