import React from "react";
import { Link } from "react-router-dom";
import { FaMapMarkerAlt, FaBed, FaBath, FaRulerCombined } from "react-icons/fa";
import "../styles/PropertyCard.css";

const PropertyCard = ({ property }) => {
    if (!property || !property._id) return null;

    const {
        title = "Premium Property",
        location = {},
        price = 0,
        propertyType = "Apartment",
        status = "Available",
        images = []
    } = property;

    const imageUrl = images.length > 0
        ? images[0]
        : "https://images.unsplash.com/photo-1570129477492-45c003edd2be?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80";

    return (
        <div className="property-card">
            <Link to={`/properties/${property._id}`} className="property-image-link">
                <div className="property-image-container">
                    <img src={imageUrl} alt={title} className="property-card-img" />
                    <div className="property-badges">
                        <span className={`badge status-${status.toLowerCase()}`}>{status}</span>
                        <span className="badge type-badge">{propertyType}</span>
                    </div>
                </div>
            </Link>

            <div className="property-card-body">
                <h3 className="property-card-title">
                    <Link to={`/properties/${property._id}`}>{title}</Link>
                </h3>

                <div className="property-card-location">
                    <FaMapMarkerAlt /> {location.city}, {location.state}
                </div>

                <div className="property-card-features">
                    <span><FaBed /> 3 Beds</span>
                    <span><FaBath /> 2 Baths</span>
                    <span><FaRulerCombined /> 1200 sqft</span>
                </div>

                <div className="property-card-divider"></div>

                <div className="property-card-footer">
                    <div className="property-card-price">
                        <span className="price-label">Price</span>
                        <span className="price-value">â‚¹{price.toLocaleString()}</span>
                    </div>
                    <Link to={`/properties/${property._id}`} className="btn-details">
                        Details
                    </Link>
                </div>
            </div>
        </div>
    );
};

export default PropertyCard;
