import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import { propertyService } from "../services/api";
import PropertyCard from "../components/PropertyCard";
import { FaHome, FaBuilding, FaHotel, FaMapMarkerAlt, FaSearch } from "react-icons/fa";
import "../styles/Home.css";

const Home = () => {
    const [featuredProperties, setFeaturedProperties] = useState([]);
    const [loading, setLoading] = useState(true);
    const [searchParams, setSearchParams] = useState({
        keyword: "",
        propertyType: "",
        city: "",
        minPrice: "",
        maxPrice: ""
    });
    
    const navigate = useNavigate();

    useEffect(() => {
        fetchFeaturedProperties();
    }, []);

    const fetchFeaturedProperties = async () => {
        try {
            const response = await propertyService.getAllProperties({ limit: 3 });
            setFeaturedProperties(response.data.data);
        } catch (err) {
            console.error("Failed to fetch featured properties", err);
        } finally {
            setLoading(false);
        }
    };

    const handleSearchChange = (e) => {
        const { name, value } = e.target;
        setSearchParams(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleSearchSubmit = (e) => {
        e.preventDefault();
        const queryParams = new URLSearchParams(searchParams).toString();
        navigate(`/properties?${queryParams}`);
    };

    return (
        <div className="home-container">
            {/* Hero Section */}
            <section className="hero-section">
                <div className="hero-content">
                    <span className="section-tag">Welcome to RealEstateHub</span>
                    <h1>Invest Today in <br />Your Dream Home</h1>
                    <p>Find your perfect property with our comprehensive real estate listings and expert guidance.</p>
                </div>
            </section>

            {/* Search Bar Container */}
            <div className="search-container">
                <form className="search-form" onSubmit={handleSearchSubmit}>
                    <div className="search-field">
                        <label>Keywords</label>
                        <input 
                            type="text" 
                            name="keyword" 
                            placeholder="Enter keywords..." 
                            value={searchParams.keyword}
                            onChange={handleSearchChange}
                        />
                    </div>
                    <div className="search-field">
                        <label>Property Type</label>
                        <select 
                            name="propertyType" 
                            value={searchParams.propertyType}
                            onChange={handleSearchChange}
                        >
                            <option value="">All Types</option>
                            <option value="House">Houses</option>
                            <option value="Apartment">Apartments</option>
                            <option value="Villa">Villas</option>
                            <option value="Plot">Plots</option>
                        </select>
                    </div>
                    <div className="search-field">
                        <label>Location</label>
                        <input 
                            type="text" 
                            name="city" 
                            placeholder="City or State" 
                            value={searchParams.city}
                            onChange={handleSearchChange}
                        />
                    </div>
                    <div className="search-field">
                        <label>Max Price</label>
                        <select 
                            name="maxPrice" 
                            value={searchParams.maxPrice}
                            onChange={handleSearchChange}
                        >
                            <option value="">Any Price</option>
                            <option value="5000000">Up to 50L</option>
                            <option value="10000000">Up to 1Cr</option>
                            <option value="20000000">Up to 2Cr</option>
                            <option value="50000000">Up to 5Cr</option>
                        </select>
                    </div>
                    <button type="submit" className="search-btn">
                        <FaSearch /> Search
                    </button>
                </form>
            </div>

            {/* Categories Section */}
            <section className="categories-section">
                <span className="section-tag">Property Types</span>
                <h2>Explore Apartment Types</h2>
                <div className="category-grid">
                    <Link to="/properties?propertyType=House" className="category-card">
                        <div className="category-icon"><FaHome /></div>
                        <h4>Houses</h4>
                        <span>Search active listings</span>
                    </Link>
                    <Link to="/properties?propertyType=Apartment" className="category-card">
                        <div className="category-icon"><FaBuilding /></div>
                        <h4>Apartments</h4>
                        <span>Find your next home</span>
                    </Link>
                    <Link to="/properties?propertyType=Villa" className="category-card">
                        <div className="category-icon"><FaHotel /></div>
                        <h4>Villas</h4>
                        <span>Luxury living spaces</span>
                    </Link>
                    <Link to="/properties?propertyType=Plot" className="category-card">
                        <div className="category-icon"><FaMapMarkerAlt /></div>
                        <h4>Plots</h4>
                        <span>Land for your future</span>
                    </Link>
                </div>
            </section>

            {/* Featured Properties Section */}
            <section className="featured-section">
                <div className="featured-header">
                    <div>
                        <span className="section-tag">Properties</span>
                        <h2>Featured Properties</h2>
                    </div>
                    <Link to="/properties" className="view-all-link">View All Properties â†’</Link>
                </div>

                {loading ? (
                    <div className="loading">Loading amazing properties...</div>
                ) : (
                    <div className="properties-grid">
                        {featuredProperties.map(property => (
                            <PropertyCard key={property._id} property={property} />
                        ))}
                        {featuredProperties.length === 0 && (
                            <div className="no-results">No featured properties at the moment.</div>
                        )}
                    </div>
                )}
            </section>
        </div>
    );
};

export default Home;
