import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import { propertyService } from "../services/api";
import { FaPlus, FaMapMarkerAlt, FaHome, FaRupeeSign } from "react-icons/fa";
import "../styles/CreateProperty.css";

const CreateProperty = () => {
    const { user } = useAuth();
    const navigate = useNavigate();
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState("");
    const [formData, setFormData] = useState({
        title: "",
        description: "",
        price: "",
        propertyType: "Apartment",
        location: {
            address: "",
            city: "",
            state: "",
            pincode: ""
        },
        owner: user?.id,
        status: "Available"
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        if (name.startsWith("location.")) {
            const field = name.split(".")[1];
            setFormData(prev => ({
                ...prev,
                location: {
                    ...prev.location,
                    [field]: value
                }
            }));
        } else {
            setFormData(prev => ({
                ...prev,
                [name]: value
            }));
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError("");

        try {
            await propertyService.createProperty(formData);
            alert("Property listing published successfully!");
            navigate("/");
        } catch (err) {
            setError(err.response?.data?.message || "Failed to create property listing");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="create-property-container">
            <div className="create-form">
                <h2>List Your Property</h2>
                {error && <div className="error-message">{error}</div>}

                <form onSubmit={handleSubmit}>
                    <div className="form-section">
                        <h3><FaHome style={{ marginRight: '10px' }} /> Basic Information</h3>

                        <div className="form-group">
                            <label>Listing Title</label>
                            <input
                                type="text"
                                name="title"
                                value={formData.title}
                                onChange={handleChange}
                                placeholder="e.g. Modern Villa with Pool"
                                required
                            />
                        </div>

                        <div className="form-group">
                            <label>Detailed Description</label>
                            <textarea
                                name="description"
                                value={formData.description}
                                onChange={handleChange}
                                rows="5"
                                placeholder="Describe the highlights and unique features of your property..."
                                required
                            />
                        </div>

                        <div className="form-row">
                            <div className="form-group">
                                <label>Price (INR)</label>
                                <div style={{ position: 'relative' }}>
                                    <FaRupeeSign style={{ position: 'absolute', left: '15px', top: '50%', transform: 'translateY(-50%)', color: 'var(--text-muted)' }} />
                                    <input
                                        type="number"
                                        name="price"
                                        value={formData.price}
                                        onChange={handleChange}
                                        style={{ paddingLeft: '40px' }}
                                        required
                                    />
                                </div>
                            </div>

                            <div className="form-group">
                                <label>Property Type</label>
                                <select
                                    name="propertyType"
                                    value={formData.propertyType}
                                    onChange={handleChange}
                                >
                                    <option value="House">House</option>
                                    <option value="Apartment">Apartment</option>
                                    <option value="Villa">Villa</option>
                                    <option value="Plot">Plot</option>
                                </select>
                            </div>
                        </div>

                        <div className="form-group">
                            <label>Availability Status</label>
                            <select
                                name="status"
                                value={formData.status}
                                onChange={handleChange}
                            >
                                <option value="Available">Available</option>
                                <option value="Pending">Pending</option>
                                <option value="Sold">Sold</option>
                            </select>
                        </div>
                    </div>

                    <div className="form-section">
                        <h3><FaMapMarkerAlt style={{ marginRight: '10px' }} /> Property Location</h3>

                        <div className="form-group">
                            <label>Street Address</label>
                            <input
                                type="text"
                                name="location.address"
                                value={formData.location.address}
                                onChange={handleChange}
                                placeholder="123 Luxury Lane"
                                required
                            />
                        </div>

                        <div className="form-row">
                            <div className="form-group">
                                <label>City</label>
                                <input
                                    type="text"
                                    name="location.city"
                                    value={formData.location.city}
                                    onChange={handleChange}
                                    required
                                />
                            </div>

                            <div className="form-group">
                                <label>State</label>
                                <input
                                    type="text"
                                    name="location.state"
                                    value={formData.location.state}
                                    onChange={handleChange}
                                    required
                                />
                            </div>

                            <div className="form-group">
                                <label>Pincode</label>
                                <input
                                    type="text"
                                    name="location.pincode"
                                    value={formData.location.pincode}
                                    onChange={handleChange}
                                    pattern="[0-9]{5,6}"
                                    required
                                />
                            </div>
                        </div>
                    </div>

                    <button type="submit" disabled={loading} className="submit-btn">
                        {loading ? "Publishing listing..." : (
                            <>
                                <FaPlus style={{ marginRight: '8px' }} /> Create Property Listing
                            </>
                        )}
                    </button>
                </form>
            </div>
        </div>
    );
};

export default CreateProperty;
